// Bundle Selection
function selectBundle(card, index) {
  document.querySelectorAll('.bundle-card').forEach((c) => c.classList.remove('selected'));
  card.classList.add('selected');
  const price = card.getAttribute('data-price');
  const ctaButton = document.querySelector('.cta-button-primary');
  if (ctaButton) {
    ctaButton.textContent = `YES‚ÄîMAKE ME UNFORGETTABLE ($${price})`;
  }
}

// Dynamic Delivery Date (5 business days from today)
function updateDeliveryDate() {
  const today = new Date();
  const deliveryDate = new Date(today);
  deliveryDate.setDate(today.getDate() + 5);
  const options = { month: 'long', day: 'numeric' };
  const formattedDate = deliveryDate.toLocaleDateString('en-US', options);
  const deliveryElement = document.querySelector('.delivery-order-by strong');
  if (deliveryElement) {
    deliveryElement.textContent = formattedDate;
  }
}

// Initialize on page load
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', updateDeliveryDate);
} else {
  updateDeliveryDate();
}
